import React, { Component } from 'react';
import { auth, db } from '../services/firebase';

export default class Chat extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: auth().currentUser,
            chats: [],
            content: '',
            readError: null,
            writeError: null
        };
    }

    async componentDidMount() {
        this.setState({ readError: null })
    }
}
